                        <div className="no-games">‡¶ï‡ßã‡¶® ‡¶ó‡ßá‡¶Æ ‡¶™‡¶æ‡¶ì‡¶Ø‡¶º‡¶æ ‡¶Ø‡¶æ‡¶Ø‡¶º‡¶®‡¶ø</div>
                      )}
                    </ul>
                  ) : (
                    // üé≤ Show providers for other categories
                    <ul>
                      {active?.uniqueProviders?.map((item, index) => (
                        <li key={index}>
                          <Link
                            to={`/games/${encodeURIComponent(
                              active.category_name
                            )}/${encodeURIComponent(item.providercode)}`}
                            className="game-link"
                          >
                            <img src={item.image_url} alt={item.company} />
                            <p>{item.company}</p>
                          </Link>
                        </li>
                      ))}
                    </ul>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
