      navigate('/login', { 
        state: { 
          from: window.location.pathname,
          message: "গেম খেলতে লগইন করুন"
        } 
      });
      if (onClose) onClose();
      return;
    }

    const result = await launchGame(game);
    console.log("Game launch result:", result);
    
    if (result.success) {
      console.log("Game launched successfully");
      if (onClose) onClose();
    } else if (result.requiresLogin) {
      navigate('/login', { 
        state: { 
          from: window.location.pathname,
          message: "গেম খেলতে লগইন করুন"
        } 
