// components/PersonalInfoPage.js
import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { useAuth } from "../../../contexts/AuthContext";

const PersonalInfoPage = ({
  showError,
  showSuccess,
  showWarning,
  showInfo,
}) => {
  const navigate = useNavigate();
  const location = useLocation();
  const { user, setUser } = useAuth();
  const [isLoading, setIsLoading] = useState(false);

  const closeModal = () => {
    if (location.state?.background) {
      navigate(-1);
    } else {
      navigate("/");
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return "";
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit'
    }).replace(/\//g, '/');
  };

  const formatPhoneNumber = (phone) => {
    if (!phone) return "";
    return `${phone.countryCode} ${phone.number}`;
  };

  const handleMyVipClick = () => {
    navigate("/vip-points-exchange", {
      state: { background: location },
    });
  };

  const handleAddPhone = () => {
    navigate("/add_phone_number", {
      state: { background: location },
    });
  };

  const handleAddEmail = () => {
    navigate("/add-email", {
      state: { background: location },
    });
  };

  const handleAddName = () => {
    navigate("/update-name", {
      state: { background: location },
    });
  };

  const handleAddBirthday = () => {
    navigate("/update-birthday", {
      state: { background: location },
    });
  };

  const handleContactCustomerService = () => {
    navigate("/live-chat", {
      state: { background: location },
    });
  };

  const handleVerifyPhone = async (phoneNumber) => {
    try {
      setIsLoading(true);
      // This would call your backend API to send verification code
      showInfo("Verification code sent to your phone");
    } catch (error) {
      showError("Failed to send verification code");
    } finally {
      setIsLoading(false);
    }
  };

  const handleSetDefaultPhone = async (phoneNumber) => {
    try {
      setIsLoading(true);
      // This would call your backend API to set default phone
      showSuccess("Default phone number updated");
    } catch (error) {
      showError("Failed to update default phone number");
    } finally {
      setIsLoading(false);
    }
  };

  if (!user) {
    return (
      <div className="popup-page-wrapper active">
        <div className="popup-page show-toolbar popup-page--active popup-page--align-top">
          <div className="popup-page__backdrop" onClick={closeModal}></div>
          <div className="popup-page__main popup-page-main popup-page-main--show">
            <div className="popup-page-main__container">
              <div className="content mcd-style new-profile player-content">
                <div className="loading-message">Loading user information...</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="popup-page-wrapper active">
      <div className="popup-page show-toolbar popup-page--active popup-page--align-top">
        <div className="popup-page__backdrop" onClick={closeModal}></div>
        <div className="popup-page__main popup-page-main popup-page-main--show">
          {/* Header */}
          <div className="popup-page-main__header">
            <div className="popup-page-main__title">ব্যাক্তিগত তথ্য</div>
            <div
              className="popup-page-main__close ng-star-inserted"
              onClick={closeModal}
            >
              ✕
            </div>
          </div>

          {/* Content */}
          <div className="popup-page-main__container">
            <div className="content mcd-style new-profile player-content third-party-login">
              {/* VIP Card Section */}
              <div className="player-vip-lv1 ng-star-inserted">
                <div
                  className="player-info-vip"
                  style={{
                    backgroundImage: 'url("https://img.s628b.com/sb/h5/assets/images/player/vip/vip-card-bg-1.jpg?v=1761636564965")',
                  }}
                >
                  <div className="member-pic">
                    <span
                      className="item-icon ng-star-inserted"
                      style={{
                        backgroundImage: 'url("https://img.s628b.com/sb/h5/assets/images/player/vip/memberpic-lv1.svg?v=1761636564965")',
                      }}
                    ></span>
                  </div>
                </div>

                <div className="membername-wrap">
                  <div className="membername">{user.userId}</div>
                  <div className="level ng-star-inserted">
                    {user.role === 'admin' ? 'Admin' : user.vipLevel || 'Copper'}
                  </div>
                  <br />
                  <div className="register-date">
                    রেজিস্ট্রেশনের তারিখ : <i>{formatDate(user.timestamp)}</i>
                  </div>
                </div>
              </div>

              {/* VIP Points Section */}
              <div id="profile-vip-div" className="menu-box ng-star-inserted">
                <div className="vip-area-group">
                  <div className="left-box">
                    <div className="item">
                      <h3>ভিআইপি পয়েন্টস (VP)</h3>
                      <div className="points-number">
                        {user.vipPoints || 0}
                      </div>
                    </div>
                  </div>
                  <div className="right-box">
                    <a
                      className="goto-myvip ng-star-inserted"
                      onClick={handleMyVipClick}
                    >
                      <div className="myvip-text">
                        <span>মাই ভিআইপি</span>
                        <span
                          className="item-icon"
                          style={{
                            maskImage: 'url("https://img.s628b.com/sb/h5/assets/images/icon-set/player/vip/icon-arrow.svg?v=1761636564965")',
                          }}
                        ></span>
                      </div>
                    </a>
                  </div>
                </div>
              </div>

              {/* Verification Tips Box for Deposit */}
              <div className="ng-trigger ng-trigger-slideAnimation ng-star-inserted">
                <div className="tips-info verify-tips tips-info-toggle ng-star-inserted">
                  <div className="title-box">
                    <h5>
                      <i 
                        className="tips-icon" 
                        style={{
                          maskImage: 'url("https://img.s628b.com/sb/h5/assets/images/icon-set/icon-tips-type02.svg?v=1761636564965")',
                        }}
                      ></i>
                      <span>ডিপোজিট অনুরোধ এগিয়ে নিতে নীচের তথ্যগুলি প্রয়োজন।</span>
                    </h5>
                    <div 
                      className="toggle-btn ng-star-inserted"
                      style={{
                        maskImage: 'url("https://img.s628b.com/sb/h5/assets/images/icon-set/icon-arrow-type09.svg?v=1761636564965")',
                      }}
                    ></div>
                  </div>
                  <ol className="tips-info-block active">
                    <li className="contact-info ng-star-inserted">
                      <a className="ng-star-inserted">
                        <label>Contact Info</label>
                        <ul>
                          <li className="ng-star-inserted">ফোন নাম্বার</li>
                        </ul>
                      </a>
                    </li>
                  </ol>
                </div>
              </div>

              {/* Verification Tips Box for Withdrawal */}
              <div className="ng-trigger ng-trigger-slideAnimation ng-star-inserted">
                <div className="tips-info verify-tips tips-info-toggle ng-star-inserted">
                  <div className="title-box">
                    <h5>
                      <i 
                        className="tips-icon" 
                        style={{
                          maskImage: 'url("https://img.s628b.com/sb/h5/assets/images/icon-set/icon-tips-type02.svg?v=1761636564965")',
                        }}
                      ></i>
                      <span>উইথড্রয়াল রিকোয়েস্ট সম্পন্ন করার আগে অনুগ্রহপূর্বক নীচের ভেরিফিকেশন সম্পূর্ণ করুন।</span>
                    </h5>
                    <div 
                      className="toggle-btn ng-star-inserted"
                      style={{
                        maskImage: 'url("https://img.s628b.com/sb/h5/assets/images/icon-set/icon-arrow-type09.svg?v=1761636564965")',
                      }}
                    ></div>
                  </div>
                  <ol className="tips-info-block active">
                    <li className="personal-info ng-star-inserted">
                      <a className="ng-star-inserted">
                        <label>ব্যাক্তিগত তথ্য</label>
                        <ul>
                          <li className="ng-star-inserted">সম্পূর্ণ নাম</li>
                        </ul>
                      </a>
                    </li>
                    <li className="contact-info ng-star-inserted">
                      <a className="ng-star-inserted">
                        <label>Contact Info</label>
                        <ul>
                          <li className="ng-star-inserted">ফোন নাম্বার</li>
                        </ul>
                      </a>
                    </li>
                  </ol>
                </div>
              </div>

              {/* Personal Information Section */}
              <div className="menu-box unverified-block-personal ng-star-inserted">
                {/* Full Name */}
                <div className="list-group" name="সম্পূর্ণ নাম">
                  <div className="icon-block">
                    <div
                      className="item-icon"
                      style={{
                        maskImage: 'url("https://img.s628b.com/sb/h5/assets/images/icon-set/icon-username.svg?v=1761636564965")',
                      }}
                    ></div>
                  </div>
                  <div className="list">
                    <div className="list-info">
                      <div className="left">
                        <div className="info-block">
                          <div>
                            <label className="title">সম্পূর্ণ নাম</label>
                            <label></label>
                          </div>
                          <label className="tips ng-star-inserted">
                            {user.name || ""}
                          </label>
                        </div>
                      </div>
                      <div className="right">
                        {!user.name && (
                          <button 
                            className="button" 
                            onClick={handleAddName}
                            disabled={isLoading}
                          >
                            অ্যাড
                          </button>
                        )}
                      </div>
                    </div>
                  </div>
                </div>

                {/* Birthday */}
                <div className="list-group" name="জন্মদিন">
                  <div className="icon-block">
                    <div
                      className="item-icon"
                      style={{
                        maskImage: 'url("https://img.s628b.com/sb/h5/assets/images/icon-set/player/icon-birthday.svg?v=1761636564965")',
                      }}
                    ></div>
                  </div>
                  <div className="list">
                    <div className="list-info">
                      <div className="left">
                        <div className="info-block">
                          <div>
                            <label className="title">জন্মদিন</label>
                            <label></label>
                          </div>
                          <label className="tips">
                            {formatDate(user.birthday)}
                          </label>
                        </div>
                      </div>
                      <div className="right">
                        {!user.birthday && (
                          <button 
                            className="button" 
                            onClick={handleAddBirthday}
                            disabled={isLoading}
                          >
                            অ্যাড
                          </button>
                        )}
                      </div>
                    </div>
                  </div>
                </div>

                {/* Phone Number */}
                <div className="list-group" name="ফোন নাম্বার">
                  <div className="icon-block">
                    <div
                      className="item-icon"
                      style={{
                        maskImage: 'url("https://img.s628b.com/sb/h5/assets/images/icon-set/player/icon-phone.svg?v=1761636564965")',
                      }}
                    ></div>
                  </div>
                  <div className="list phone">
                    {user.phone && user.phone.map((phone, index) => (
                      <div key={index} className="list-info ng-star-inserted">
                        <div className="left">
                          <div className="info-block">
                            <div>
                              <label className="title">
                                ফোন নাম্বার {index > 0 ? `#${index + 1}` : ""}
                              </label>
                              {phone.isDefault && (
                                <label className="tag ng-star-inserted">
                                  Default
                                </label>
                              )}
                            </div>
                            <label className="tips">
                              {formatPhoneNumber(phone)}
                            </label>
                          </div>
                        </div>
                        <div className="right">
                          <div className={`status ${phone.verified ? 'verified' : 'unconfirm-btn'} ng-star-inserted`}>
                            {phone.verified ? "Verified" : "যাচাই করা হয়নি"}
                          </div>
                          {!phone.isDefault && (
                            <button 
                              className="button default-btn"
                              onClick={() => handleSetDefaultPhone(phone.number)}
                              disabled={isLoading}
                            >
                              Set Default
                            </button>
                          )}
                        </div>
                      </div>
                    ))}
                    {(!user.phone || user.phone.length === 0) && (
                      <a
                        className="add-phone-btn"
                        onClick={handleAddPhone}
                      >
                        <div className="icon-add-phone-btn"></div>
                        <p>অ্যাড</p>
                      </a>
                    )}
                  </div>
                </div>

                {/* Email */}
                <div id="profile-email" className="list-group" name="ই-মেইল">
                  <div className="icon-block">
                    <div
                      className="item-icon"
                      style={{
                        maskImage: 'url("https://img.s628b.com/sb/h5/assets/images/icon-set/player/icon-email.svg?v=1761636564965")',
                      }}
                    ></div>
                  </div>
                  <div className="list">
                    <div className="list-info ng-star-inserted">
                      <div className="left">
                        <div className="info-block">
                          <div>
                            <label className="title">ই-মেইল</label>
                            {user.isVerified?.email && (
                              <label className="tag ng-star-inserted">
                                Verified
                              </label>
                            )}
                          </div>
                          <label className="tips">
                            {user.email || ""}
                          </label>
                        </div>
                      </div>
                      <div className="right">
                        {!user.email ? (
                          <button 
                            className="button" 
                            onClick={handleAddEmail}
                            disabled={isLoading}
                          >
                            অ্যাড
                          </button>
                        ) : !user.isVerified?.email && (
                          <button 
                            className="button verify-btn"
                            disabled={isLoading}
                          >
                            Verify
                          </button>
                        )}
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Footer Note */}
              <div className="member-menu-point ng-star-inserted">
                <i>
                  <span
                    className="item-icon"
                    style={{
                      backgroundImage: 'url("https://img.s628b.com/sb/h5/assets/images/icon-set/theme-icon/icon-customer.png?v=1761636564965")',
                    }}
                  ></span>
                </i>
                <p>
                  গোপনীয়তা এবং নিরাপত্তার জন্য, নিশ্চিতকরণের পরে তথ্য পরিবর্তন করা যাবে না। 
                  অনুগ্রহ করে{" "}
                  <span
                    name="liveChatBtn"
                    onClick={handleContactCustomerService}
                    style={{ cursor: 'pointer', color: '#007bff', textDecoration: 'underline' }}
                  >
                    গ্রাহক পরিষেবার সাথে যোগাযোগ করুন
                  </span>
                  .
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PersonalInfoPage;
