// components/member/Funds/Deposit.jsx
import React, { useState, useEffect } from "react";
import { useApp } from "../../../contexts/AppContext";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { useAuth } from "../../../contexts/AuthContext";
import VerificationTips from "../../common/VerificationTips/VerificationTips";
import PaymentMethodList from "./PaymentMethodList";

const Deposit = ({
  showError,
  showSuccess,
  showWarning,
  showInfo,
}) => {
  const [amount, setAmount] = useState("");
  const [paymentMethod, setPaymentMethod] = useState("");
  const [selectedGateway, setSelectedGateway] = useState(null);
  const [promotion, setPromotion] = useState("");
  const [depositChannel, setDepositChannel] = useState("");
  const [paymentType, setPaymentType] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showGatewayList, setShowGatewayList] = useState(false);
  const [gatewayStats, setGatewayStats] = useState(null);

  const { user } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const { promotions, fetchPromotions, setNotificationFunctions } = useApp();

  // Check verification status
  const hasVerifiedPhone = user?.phone?.some((phone) => phone.verified) || false;
  const isBlocked = !hasVerifiedPhone;

  // Set notification functions in context
  useEffect(() => {
    if (setNotificationFunctions) {
      setNotificationFunctions({
        showError,
        showSuccess,
        showWarning,
        showInfo,
      });
    }
  }, [setNotificationFunctions, showError, showSuccess, showWarning, showInfo]);

  const quickAmounts = [100, 1000, 2000, 5000, 10000, 15000, 20000, 30000];

  const depositChannels = [
    { id: "3367", name: "EP-ক্যাশ আউট", type: "cashout" },
    { id: "3371", name: "AP-ক্যাশ আউট", type: "cashout" },
    { id: "3705", name: "SP-ক্যাশ আউট", type: "cashout" },
    { id: "3508", name: "EP-সেন্ড মানি", type: "sendmoney" },
  ];

  // Filter and prepare promotions
  const getFilteredPromotions = () => {
    const depositPromos = promotions.deposit || [];
    return depositPromos;
  };

  const filteredPromotions = getFilteredPromotions();

  // Set default promotion
  useEffect(() => {
    if (filteredPromotions.length > 0 && !promotion) {
      setPromotion(filteredPromotions[0]._id);
    }
  }, [filteredPromotions]);

  const getSelectedPromotion = () => {
    return filteredPromotions.find((promo) => promo._id === promotion);
  };

  const calculateBonus = () => {
    if (isBlocked) return 0;

    const selectedPromo = getSelectedPromotion();
    if (!selectedPromo || !amount || isNaN(amount)) return 0;

    const depositAmount = parseFloat(amount);
    if (depositAmount < (selectedPromo.minDeposit || 0)) return 0;

    if (selectedPromo.fixedAmount) {
      return Math.min(
        selectedPromo.fixedAmount,
        selectedPromo.maxBonus || Infinity
      );
    }

    const bonus = (depositAmount * (selectedPromo.percentage || 0)) / 100;
    return selectedPromo.maxBonus
      ? Math.min(bonus, selectedPromo.maxBonus)
      : bonus;
  };

  const getTotalAmount = () => {
    if (isBlocked) return 0;

    const depositAmount = parseFloat(amount) || 0;
    return depositAmount + calculateBonus();
  };

  const validateForm = () => {
    if (isBlocked) {
      return {
        isValid: false,
        message: "Please complete phone verification first",
      };
    }

    if (!amount || isNaN(amount)) {
      return { isValid: false, message: "Please enter a valid amount" };
    }

    const depositAmount = parseFloat(amount);
    
    // Check minimum amount from selected gateway or default
    const minAmount = selectedGateway?.minimun_amount || 100;
    if (depositAmount < minAmount) {
      return { 
        isValid: false, 
        message: `Minimum deposit amount is ৳${minAmount}` 
      };
    }

    // Check maximum amount from selected gateway or default
    const maxAmount = selectedGateway?.maximum_amount || 30000;
    if (depositAmount > maxAmount) {
      return { 
        isValid: false, 
        message: `Maximum deposit amount is ৳${maxAmount}` 
      };
    }

    if (!selectedGateway) {
      return { isValid: false, message: "Please select a payment gateway" };
    }

    if (!depositChannel) {
      return { isValid: false, message: "Please select a deposit channel" };
    }

    const selectedPromo = getSelectedPromotion();
    if (selectedPromo?.minDeposit && depositAmount < selectedPromo.minDeposit) {
      return {
        isValid: false,
        message: `This promotion requires minimum deposit of ৳${selectedPromo.minDeposit}`,
      };
    }

    // Check if gateway is active and within operating hours
    if (selectedGateway) {
      const now = new Date();
      const currentTotalMinutes = now.getHours() * 60 + now.getMinutes();
      
      if (!selectedGateway.is_active) {
        return {
          isValid: false,
          message: "Selected payment gateway is currently inactive"
        };
      }

      if (currentTotalMinutes < selectedGateway.startTotalMinutes || 
          currentTotalMinutes > selectedGateway.endTotalMinutes) {
        return {
          isValid: false,
          message: `This gateway is only available from ${selectedGateway.start_time} to ${selectedGateway.end_time}`
        };
      }
    }

    return { isValid: true };
  };

  const handleGatewaySelect = (gateway) => {
    setSelectedGateway(gateway);
    setShowGatewayList(false);
    
    // Auto-set payment method based on gateway type
    const methodMap = {
      'bkash': 'bkash',
      'nagad': 'nagad', 
      'rocket': 'rocket',
      'upay': 'upay',
      'usdt': 'usdt_trc20'
    };
    
    const methodKey = gateway.gateway_name.toLowerCase();
    if (methodMap[methodKey]) {
      setPaymentMethod(methodMap[methodKey]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (isSubmitting || isBlocked) return;

    const validation = validateForm();
    if (!validation.isValid) {
      showError("Error", validation.message);
      return;
    }

    setIsSubmitting(true);

    try {
      const selectedPromo = getSelectedPromotion();
      const depositAmount = parseFloat(amount);

      // Prepare deposit data
      const depositData = {
        amount: depositAmount,
        paymentMethod: selectedGateway.gateway_name,
        gatewayId: selectedGateway._id,
        gatewayNumber: selectedGateway.gateway_Number,
        gatewayOwner: selectedGateway.ownerName,
        gatewayLevel: selectedGateway.ownerLevel,
        promotion: selectedPromo?._id,
        promotionName: selectedPromo?.name,
        bonusAmount: calculateBonus(),
        totalAmount: getTotalAmount(),
        depositChannel,
        paymentType: paymentType || `${paymentMethod}_payment`,
        timestamp: new Date().toISOString(),
        status: "pending",
        userId: user.userId,
      };

      // Send to deposit API
      const response = await fetch('/api/deposit/submit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(depositData)
      });

      const result = await response.json();

      if (result.success) {
        showSuccess(
          "Deposit Request Submitted",
          `Your deposit of ৳${depositAmount.toLocaleString()} has been submitted successfully!` +
            (calculateBonus() > 0
              ? ` You will receive ৳${calculateBonus().toLocaleString()} bonus.`
              : "") +
            ` Transaction ID: ${result.transactionId}`
        );

        // Reset form
        setAmount("");
        setSelectedGateway(null);
        setPaymentMethod("");
        setDepositChannel("");
        setPaymentType("");
      } else {
        throw new Error(result.message);
      }

    } catch (error) {
      console.error("Deposit error:", error);
      showError(
        "Deposit Failed",
        error.message || "Failed to process deposit. Please try again."
      );
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleQuickAmount = (amt) => {
    if (isBlocked) return;
    
    // Check if amount is within gateway limits
    if (selectedGateway) {
      const minAmount = selectedGateway.minimun_amount || 100;
      const maxAmount = selectedGateway.maximum_amount || 30000;
      
      if (amt < minAmount) {
        showWarning("Minimum Amount", `Minimum amount for this gateway is ৳${minAmount}`);
        return;
      }
      if (amt > maxAmount) {
        showWarning("Maximum Amount", `Maximum amount for this gateway is ৳${maxAmount}`);
        return;
      }
    }
    
    setAmount(amt.toString());
  };

  const handleAmountChange = (value) => {
    if (isBlocked) return;

    // Allow only numbers and decimal point
    const validatedValue = value.replace(/[^0-9.]/g, "");

    // Ensure only one decimal point
    const parts = validatedValue.split(".");
    if (parts.length > 2) {
      return;
    }

    // Limit to 2 decimal places
    if (parts[1] && parts[1].length > 2) {
      return;
    }

    setAmount(validatedValue);
  };

  const handleVerifyNow = () => {
    navigate("/profile/personal-info", {
      state: { background: location },
    });
  };

  const getAmountRangeText = () => {
    if (selectedGateway) {
      const min = selectedGateway.minimun_amount || 100;
      const max = selectedGateway.maximum_amount || 30000;
      return `৳ ${min.toLocaleString()} - ৳ ${max.toLocaleString()}`;
    }
    return "৳ 100.00 - ৳ 30,000.00";
  };

  return (
    <div className="deposit-content">
      <div className="player-deposit-wrap">
        <div className="player-deposit-step1">
          {/* Loading State */}
          {promotions.loading && (
            <div className="loading-state">
              <div className="loading-spinner"></div>
              <p>Loading promotions...</p>
            </div>
          )}

          {/* Error State */}
          {promotions.error && (
            <div className="error-state">
              <p>Error loading promotions: {promotions.error}</p>
              <button onClick={fetchPromotions} className="retry-btn">
                Retry
              </button>
            </div>
          )}

          {/* Promotion Selection */}
          <div className="option-group select-bar">
            <label>
              <span
                className="item-icon"
                style={{
                  backgroundImage:
                    'url("/assets/images/icon-set/icon-selectpromotion.svg")',
                }}
              ></span>
              <div>প্রচার নির্বাচন করুন</div>
            </label>
            <div className="option-wrap">
              <select
                value={promotion}
                onChange={(e) => setPromotion(e.target.value)}
                className="promotion-select"
                disabled={promotions.loading || isBlocked}
              >
                {filteredPromotions.map((promo) => (
                  <option key={promo._id} value={promo._id}>
                    {promo.name}
                    {promo.percentage > 0 && ` - ${promo.percentage}% Bonus`}
                    {promo.fixedAmount > 0 &&
                      ` - ৳${promo.fixedAmount} Fixed Bonus`}
                    {promo.minDeposit > 0 && ` (Min: ৳${promo.minDeposit})`}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <VerificationTips user={user} type="deposit" />

          {/* Payment Gateway Selection */}
          <div className="menu-box">
            <div className="title">
              <h2>
                <span>পেমেন্ট গেটওয়ে</span>
                {gatewayStats && (
                  <small className="gateway-stats">
                    Available: L1({gatewayStats.levelOneGateways}) | L2({gatewayStats.levelTwoGateways}) | L3({gatewayStats.levelThreeGateways})
                  </small>
                )}
              </h2>
            </div>
            
            {isBlocked && (
              <div className="kyc-verify-mask ng-star-inserted">
                <div className="kyc-verify-mask-icon"></div>
                <div className="kyc-verify-mask-message">
                  অনুগ্রহ করে যাচাইকরণ সম্পন্ন করুন।
                </div>
                <div className="kyc-verify-mask-blur"></div>
                <div className="kyc-verify-mask-action">
                  <button className="verify-now-btn" onClick={handleVerifyNow}>
                    Verify Now
                  </button>
                </div>
              </div>
            )}

            {/* Selected Gateway Display */}
            {selectedGateway && !showGatewayList && (
              <div className="selected-gateway">
                <div className="gateway-card selected">
                  <div className="method-header">
                    <img
                      src={selectedGateway.image_url || "/assets/images/icon-set/default-payment.png"}
                      alt={selectedGateway.gateway_name}
                      className="method-icon"
                    />
                    <div className="method-info">
                      <h4 className="method-name">{selectedGateway.gateway_name}</h4>
                      <p className="method-number">{selectedGateway.gateway_Number}</p>
                      <div className="gateway-meta">
                        <span className="method-level">Level {selectedGateway.ownerLevel}</span>
                        <span className="method-owner">by {selectedGateway.ownerName}</span>
                      </div>
                      <div className="gateway-limits">
                        <span>Min: ৳{selectedGateway.minimun_amount?.toLocaleString() || '100'}</span>
                        <span>Max: ৳{selectedGateway.maximum_amount?.toLocaleString() || '30,000'}</span>
                      </div>
                    </div>
                  </div>
                  <button
                    type="button"
                    className="change-gateway-btn"
                    onClick={() => setShowGatewayList(true)}
                    disabled={isSubmitting}
                  >
                    Change
                  </button>
                </div>
              </div>
            )}

            {/* Gateway List */}
            {(!selectedGateway || showGatewayList) && !isBlocked && (
              <PaymentMethodList
                showError={showError}
                showSuccess={showSuccess}
                onPaymentMethodSelect={handleGatewaySelect}
                selectedMethod={selectedGateway}
                type="deposit"
                onStatsUpdate={setGatewayStats}
              />
            )}
          </div>

          {/* Payment Type - Show only for mobile payments */}
          {!isBlocked && selectedGateway && selectedGateway.payment_type === "mobile" && (
            <div className="select-group">
              <div className="title">
                <h2>
                  <span>Payment Type</span>
                </h2>
              </div>
              <ul className="col2">
                <li className="payment-type-item">
                  <input
                    type="radio"
                    name="paymentType"
                    id="paymentType_payment"
                    checked={paymentType === `${paymentMethod}_payment`}
                    onChange={() => setPaymentType(`${paymentMethod}_payment`)}
                    disabled={isSubmitting || isBlocked}
                  />
                  <label htmlFor="paymentType_payment">
                    <span>{selectedGateway.gateway_name} Payment</span>
                    <span className="item-icon"></span>
                  </label>
                </li>
                <li className="payment-type-item">
                  <input
                    type="radio"
                    name="paymentType"
                    id="paymentType_sendmoney"
                    checked={paymentType === `${paymentMethod}_sendmoney`}
                    onChange={() => setPaymentType(`${paymentMethod}_sendmoney`)}
                    disabled={isSubmitting || isBlocked}
                  />
                  <label htmlFor="paymentType_sendmoney">
                    <span>{selectedGateway.gateway_name} Send Money</span>
                    <span className="item-icon"></span>
                  </label>
                </li>
              </ul>
            </div>
          )}

          {/* Deposit Channel */}
          {!isBlocked && selectedGateway && (
            <div className="deposit-normal">
              <div className="menu-box">
                <div className="title">
                  <h2>
                    <span>Deposit Channel</span>
                  </h2>
                </div>
                <div className="select-group checkbox-style checkbox-height-set">
                  <ul className="col2">
                    {depositChannels.map((channel) => (
                      <li key={channel.id}>
                        <input
                          type="radio"
                          name="depositSetting"
                          id={`depositSetting_${channel.id}`}
                          checked={depositChannel === channel.id}
                          onChange={() => setDepositChannel(channel.id)}
                          disabled={isSubmitting || isBlocked}
                        />
                        <label htmlFor={`depositSetting_${channel.id}`}>
                          <span>{channel.name}</span>
                          <span className="item-icon"></span>
                        </label>
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>
          )}

          {/* Amount Selection */}
          {!isBlocked && selectedGateway && (
            <div className="menu-box active">
              <div className="title">
                <h2>
                  <span>Amount</span>
                  <i>{getAmountRangeText()}</i>
                </h2>
              </div>

              <div className="select-group style-add-amount">
                <ul className="col4">
                  {quickAmounts.map((amt, index) => {
                    const minAmount = selectedGateway.minimun_amount || 100;
                    const maxAmount = selectedGateway.maximum_amount || 30000;
                    const isDisabled = amt < minAmount || amt > maxAmount;
                    
                    return (
                      <li key={index}>
                        <input
                          type="radio"
                          name="depositAmount"
                          id={`depositAmount_${index}`}
                          checked={amount === amt.toString()}
                          onChange={() => handleQuickAmount(amt)}
                          disabled={isSubmitting || isBlocked || isDisabled}
                        />
                        <label 
                          htmlFor={`depositAmount_${index}`}
                          className={isDisabled ? "disabled" : ""}
                          title={isDisabled ? `Amount must be between ৳${minAmount} and ৳${maxAmount}` : ""}
                        >
                          <span>৳{amt.toLocaleString()}</span>
                        </label>
                      </li>
                    );
                  })}
                </ul>
              </div>

              <div className="input-group money">
                <label htmlFor="amount">৳</label>
                <div className="input-wrap">
                  <input
                    type="text"
                    id="amount"
                    placeholder="0.00"
                    value={amount}
                    onChange={(e) => handleAmountChange(e.target.value)}
                    onBlur={(e) => {
                      if (isBlocked) return;
                      const value = parseFloat(e.target.value);
                      const minAmount = selectedGateway.minimun_amount || 100;
                      const maxAmount = selectedGateway.maximum_amount || 30000;
                      
                      if (value && value < minAmount) {
                        showWarning(
                          "Minimum Amount",
                          `Minimum deposit amount for this gateway is ৳${minAmount}`
                        );
                      }
                      if (value && value > maxAmount) {
                        showWarning(
                          "Maximum Amount",
                          `Maximum deposit amount for this gateway is ৳${maxAmount}`
                        );
                      }
                    }}
                    inputMode="numeric"
                    disabled={isSubmitting || isBlocked}
                  />
                  {amount && !isBlocked && (
                    <button
                      type="button"
                      className="delete-btn"
                      onClick={() => setAmount("")}
                      disabled={isSubmitting}
                    >
                      ×
                    </button>
                  )}
                </div>
              </div>

              {/* Bonus Calculation */}
              {!isBlocked &&
                amount &&
                !isNaN(amount) &&
                parseFloat(amount) >= (selectedGateway.minimun_amount || 100) && (
                  <div className="bonus-calculation">
                    <div className="calculation-row">
                      <span>Deposit Amount:</span>
                      <span>৳{parseFloat(amount).toLocaleString()}</span>
                    </div>
                    {calculateBonus() > 0 && (
                      <div className="calculation-row bonus">
                        <span>Bonus Amount:</span>
                        <span>+ ৳{calculateBonus().toLocaleString()}</span>
                      </div>
                    )}
                    <div className="calculation-row total">
                      <span>Total Credit:</span>
                      <span className="total-amount">
                        ৳{getTotalAmount().toLocaleString()}
                      </span>
                    </div>
                  </div>
                )}

              {/* Gateway Instructions */}
              <div className="tips-info note">
                <h5>
                  <i className="tips-icon"></i>
                  <span>
                    Selected Gateway: {selectedGateway.gateway_name} ({selectedGateway.gateway_Number}) - Level {selectedGateway.ownerLevel}
                    <br />
                    Operating Hours: {selectedGateway.start_time} - {selectedGateway.end_time}
                    <br />
                    ১.ক্যাশ আউট বা সেন্ডমানি করার আগে 'ব্যক্তিগত তথ্য' অংশে সর্বোচ্চ ৩টি মোবাইল নম্বর যোগ করে ভেরিফাই করুন।
                    ২.আপনার ডিপোজিট প্রক্রিয়াটি আরও দ্রুত সফল করতে সঠিক ক্যাশ আউট নাম্বার,এমাউন্ট এবং ট্রানজেকশন আইডি সহ সাবমিট করুন।
                    ৩.যেকোনো ডিপোজিট করার আগে সবসময় আমাদের ডিপোজিট পেইজে নাম্বার চেক করুন।
                    ৪.ডিপোজিট পেন্ডিং থাকা অবস্থায় আপনি সর্বোচ্চ ২টি ডিপোজিট ট্রাই করতে পারবেন। কোনো সমস্যা হলে অনুগ্রহ করে লাইভচ্যাটের মাধ্যমে সহায়তা নিন।
                  </span>
                </h5>
              </div>
            </div>
          )}

          {/* Submit Button */}
          <div className="member-content">
            <div className="button">
              <Link
                className={`submit-btn ${
                  !amount || !selectedGateway || !depositChannel || isSubmitting
                    ? "disabled"
                    : ""
                }`}
                onClick={handleSubmit}
                disabled={
                  !amount ||
                  !selectedGateway ||
                  !depositChannel ||
                  isSubmitting ||
                  isBlocked
                }
              >
                {isSubmitting ? (
                  <>
                    <div className="submit-spinner"></div>
                    Processing...
                  </>
                ) : isBlocked ? (
                  "Complete Verification First"
                ) : (
                  "সাবমিট"
                )}
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Deposit;
