// Route Renderer Component
const RouteRenderer = ({ routes, isPopup = false, notificationProps }) => {
  return routes.map((route) => {
    const {
      path,
      component: Component,
      protected: isProtected = false,
      publicOnly = false,
      layout: Layout = PopupLayout,
      nested = false,
      popup = false,
      backButton = false,
      className = '',
      title = ''
    } = route;

    // Create the base element with proper layout
    let element;

    if (publicOnly) {
      element = (
        <PublicRoute restricted={true}>
          <Layout 
            showBackButton={backButton}
            className={className}
            title={title}
          >
            <Component {...notificationProps} />
          </Layout>
        </PublicRoute>
      );
    } else if (isProtected) {
      element = (
        <ProtectedRoute>
          <Layout 
            showBackButton={backButton}
            className={className}
            title={title}
          >
            <Component {...notificationProps} />
          </Layout>
        </ProtectedRoute>
      );
    } else {
      element = (
        <Layout 
          showBackButton={backButton}
          className={className}
          title={title}
        >
          <Component {...notificationProps} />
        </Layout>
      );
    }

    // Wrap with PublicLayout for public routes in main routes
    if (!isPopup && (publicOnly || path === '/promotion')) {
      element = (
        <PublicLayout>
          {element}
        </PublicLayout>
      );
    }

    return (
      <Route
        key={path}
        path={path}
        element={element}
      />
    );
  });
};

// Nested Route Renderer for RootLayout
const NestedRouteRenderer = ({ routes, notificationProps }) => {
  return routes.map((route) => {
    const { path, component: Component } = route;

    return (
      <Route
        key={path}
        path={path}
        element={<Component {...notificationProps} />}
      />
    );
  });
};

// Main App Content Component
function AppContent() {
  const location = useLocation();
  const background = location.state?.background;
  const { gameLaunchState, closeGame } = useApp();
  const notificationState = useNotificationState();

  const notificationProps = {
    showError: notificationState.showError,
    showSuccess: notificationState.showSuccess,
    showWarning: notificationState.showWarning,
    showInfo: notificationState.showInfo
  };

  return (
    <NotificationContext.Provider value={notificationProps}>
      {/* Main Routes */}
      <Routes location={background || location}>
        {/* Root Layout Routes with nested structure */}
        <Route path="/" element={
          <RootLayout {...notificationProps} />
        }>
          <Route index element={<HomePage {...notificationProps} />} />
          <Route 
            path="gamesProvidersPage/:category_name/:providercode" 
            element={<GamesProvidersPage {...notificationProps} />} 
          />
        </Route>

        {/* Standalone Public Routes */}
        <Route path="/login" element={
          <PublicRoute restricted={true}>
            <PublicLayout>
              <PopupLayout>
                <LoginPage {...notificationProps} />
              </PopupLayout>
            </PublicLayout>
          </PublicRoute>
        } />

        <Route path="/register" element={
          <PublicRoute restricted={true}>
            <PublicLayout>
              <PopupLayout>
                <RegisterPage {...notificationProps} />
              </PopupLayout>
            </PublicLayout>
          </PublicRoute>
        } />

        {/* Promotion as public route */}
        <Route path="/promotion" element={
          <PublicLayout>
            <PopupLayout>
              <PromotionPage {...notificationProps} />
            </PopupLayout>
          </PublicLayout>
        } />

        {/* 404 Page */}
        <Route path="*" element={
          <PopupLayout>
            <div className="error-page">
              <h2>Page Not Found</h2>
              <p>The page you're looking for doesn't exist.</p>
            </div>
          </PopupLayout>
        } />
      </Routes>

      {/* Popup Routes */}
      {background && (
        <Routes>
          {/* Public Popup Routes */}
          <Route path="/login" element={
            <PublicRoute restricted={true}>
              <PopupLayout>
                <LoginPage {...notificationProps} />
              </PopupLayout>
            </PublicRoute>
          } />

          <Route path="/register" element={
            <PublicRoute restricted={true}>
              <PopupLayout>
                <RegisterPage {...notificationProps} />
              </PopupLayout>
            </PublicRoute>
          } />

          <Route path="/promotion" element={
            <PopupLayout>
              <PromotionPage {...notificationProps} />
            </PopupLayout>
          } />

          {/* Protected Popup Routes */}
          <RouteRenderer 
            routes={routeConfigs.protected} 
            isPopup={true}
            notificationProps={notificationProps}
          />
        </Routes>
      )}

      {/* Game Launch Popup */}
      <ProtectedRoute>
        <GameLaunchPopup
          show={gameLaunchState?.show}
          onClose={closeGame}
          gameUrl={gameLaunchState?.gameUrl}
          userName={gameLaunchState?.userName}
        />
      </ProtectedRoute>
    </NotificationContext.Provider>
  );
}

// Notification Wrapper Component
const AppWithNotifications = ({ children }) => {
  const notificationState = useNotificationState();

  return (
    <>
      <NotificationContext.Provider value={{
        showError: notificationState.showError,
        showSuccess: notificationState.showSuccess,
        showWarning: notificationState.showWarning,
        showInfo: notificationState.showInfo
      }}>
        {children}
        <Notification
          isOpen={notificationState.notification.isOpen}
          onClose={notificationState.hideNotification}
          title={notificationState.notification.title}
          message={notificationState.notification.message}
          type={notificationState.notification.type}
          autoClose={notificationState.notification.autoClose}
          autoCloseDuration={notificationState.notification.autoCloseDuration}
          position={notificationState.notification.position}
        />
      </NotificationContext.Provider>
    </>
  );
};

// Main App Component
function App() {
  return (
    <AuthProvider>
      <AppProvider>
        <PopupProvider>
          <AppWithNotifications>
            <AppContent />
          </AppWithNotifications>
        </PopupProvider>
      </AppProvider>
    </AuthProvider>
  );
}

export default App;
