// Loading Component
const LoadingSpinner = () => (
  <div className="loading-fullscreen">
    <div className="spinner"></div>
    <p>লোড হচ্ছে...</p>
  </div>
);

// Route Protection Components
const ProtectedRoute = ({ children, roles = [] }) => {
  const { isAuthenticated, isLoading, user } = useAuth();
  const location = useLocation();

  if (isLoading) return <LoadingSpinner />;

  if (!isAuthenticated) {
    return <Navigate to="/login" state={{ from: location }} replace />;
  }

  if (roles.length > 0 && user?.role && !roles.includes(user.role)) {
    return <Navigate to="/" replace />;
  }

  return children;
};

const PublicRoute = ({ children }) => {
  const { isAuthenticated, isLoading } = useAuth();

  if (isLoading) return <LoadingSpinner />;

  if (isAuthenticated) {
    return <Navigate to="/" replace />;
  }

  return children;
};

const PublicOnlyRoute = ({ children }) => {
  const { isLoading } = useAuth();

  if (isLoading) return <LoadingSpinner />;

  return children;
};

// Notification Hook
const useNotification = () => {
  const notificationState = useNotificationState();

  return {
    showError: notificationState.showError,
    showSuccess: notificationState.showSuccess,
    showWarning: notificationState.showWarning,
    showInfo: notificationState.showInfo,
    notification: notificationState.notification,
    hideNotification: notificationState.hideNotification
  };
};

// Route Configuration
