// Dynamic Route Renderer
const renderRoutes = (routes, notificationProps, background = null) => {
  return routes.map((route) => {
    const RouteElement = route.element;
    const Layout = route.layout;

    const routeElement = (
      <Layout key={route.path} {...(route.layoutProps || {})}>
        <RouteElement {...notificationProps} />
      </Layout>
    );

    let wrappedElement = routeElement;

    // Apply route protection
    switch (route.protection) {
      case 'protected':
        wrappedElement = (
          <ProtectedRoute key={route.path}>
            {routeElement}
          </ProtectedRoute>
        );
        break;
      case 'public':
        wrappedElement = (
          <PublicRoute key={route.path}>
            {routeElement}
          </PublicRoute>
        );
        break;
      case 'public-only':
        wrappedElement = (
          <PublicOnlyRoute key={route.path}>
            {routeElement}
          </PublicOnlyRoute>
        );
        break;
      default:
        wrappedElement = routeElement;
    }

    return (
      <Route
        key={route.path}
        path={route.path}
        element={wrappedElement}
      />
    );
  });
};

// App Content Component
const AppContent = () => {
  const location = useLocation();
  const background = location.state?.background;
  const { gameLaunchState, closeGame } = useApp();
  const notification = useNotification();

  const notificationProps = {
    showError: notification.showError,
    showSuccess: notification.showSuccess,
    showWarning: notification.showWarning,
    showInfo: notification.showInfo
  };

  return (
    <>
      {/* Main Routes */}
      <Routes location={background || location}>
        {/* Public Routes with RootLayout */}
        <Route path="/" element={
          <RootLayout {...notificationProps} />
        }>
          {renderRoutes(routeConfig.public, notificationProps)}
        </Route>
      </Routes>

      {/* Popup Routes */}
      {background && (
        <Routes>
          {renderRoutes([...routeConfig.auth, ...routeConfig.protected], notificationProps, background)}
        </Routes>
      )}

      {/* Game Launch Popup */}
      <ProtectedRoute>
        <GameLaunchPopup
          show={gameLaunchState?.show}
          onClose={closeGame}
          gameUrl={gameLaunchState?.gameUrl}
          userName={gameLaunchState?.userName}
        />
      </ProtectedRoute>
    </>
  );
};

// App with Notifications Wrapper
const AppWithNotifications = ({ children }) => {
  const notification = useNotification();

  return (
    <>
      {children}
      <Notification
        isOpen={notification.notification.isOpen}
        onClose={notification.hideNotification}
        title={notification.notification.title}
        message={notification.notification.message}
        type={notification.notification.type}
        autoClose={notification.notification.autoClose}
        autoCloseDuration={notification.notification.autoCloseDuration}
        position={notification.notification.position}
      />
    </>
  );
};

// Main App Component
function App() {
  return (
    <AuthProvider>
      <AppProvider>
        <PopupProvider>
          <AppWithNotifications>
            <AppContent />
          </AppWithNotifications>
        </PopupProvider>
      </AppProvider>
    </AuthProvider>
  );
}

export default App;
